roveAd(createdBy, adId, updatedBy) {            let form = new FormData();            form.append('id', adId);            form.append('userid', createdBy);                        form.append('createdBy', updatedBy);                        form.append('updatedBy', updatedBy);                        form.append('paymentReceivedBy', $("#paymentReceivedBy").val());            form.append('_token', '{{ csrf_token() }}');            apex("POST", "{{ url('admin/api/ad/success') }}", form, '', "/admin/ads-pending", "#");        }                function rejectAd(createdBy, adId, updatedBy) {            let form = new FormData();            form.append('id', adId);            form.append('createdBy', createdBy);                        form.append('updatedBy', updatedBy);            form.append('_token', '{{ csrf_token() }}');            apex("POST", "{{ url('admin/api/ad/cancel') }}", form, '', "/admin/ads-pending", "#");        }    </script>@endsection